import "./AboutContentStyles.css";
import React from 'react';
import { Link } from "react-router-dom";
import React1 from "../assets/react1.jpg";
import ResumePDF from "../assets/resume.pdf"

const AboutContent = () => {
  return (
    <div className="about">
      <div className="left">
        <h2 className="about-heading">About Me</h2>
        <h1>I DON'T PANIC.....</h1>
        <p>
          I'm a Front-End Web Developer who's passionate about creating high-end
          designs for websites and making them highly secure and interactive.
        </p>
        <p
          style={{
            fontFamily: "monospace",
            fontSize: "1.5rem",
            textAlign: "center",
          }}
        >
          My Tech Stack Includes:
        </p>
        <p
          style={{
            fontFamily: "monospace",
            fontSize: "1.5rem",
            textAlign: "center",
            textIndent:"-90px",
          }}
        >
          React||MERN||Tailwind.css||SASS||Bootstrap
        </p>
        <Link to="/contact">
          <button className="btn">Contact</button>
        </Link>
        <a href={ResumePDF} download="resume.pdf">
  <button style={{ marginTop: "10px" }} className="btn">
    Download Resume
  </button>
</a>
      </div>
      <div className="right">
        <div className="img-container">
          <div className="img-stack top">
            <img src={React1} className="img" alt="true" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default AboutContent;
